	//This method should be called every turn since effects might change the unit's attributes.
	void calculateAttributes(){
		this.totalHP=this.HP+this.HPMods;
		this.totalMP=this.MP+this.MPMods;
		this.totalDP=this.DP+this.DPMods;
		this.totalSTR=this.STR+this.STRMods;
		this.totalDEX=this.DEX+this.DEXMods;
		this.totalFIT=this.FIT+this.FITMods;
		this.totalMAG=this.MAG+this.MAGMods;
		this.totalSpeed=this.speed+this.speedMods;
		this.totalMovementPoints=this.movementPoints+this.movementPointMods;
	}
	//This method generally only needs to be called after the Entity changes class, race, type, or level.
	void calculateMaxGrowths(){
		double newMaxHPGrowth = 0;
		double newMaxMPGrowth = 0;
		double newMaxDPGrowth = 0;
		double newMaxSTRGrowth = 0;
		double newMaxDEXGrowth = 0;
		double newMaxFITGrowth = 0;
		double newMaxMAGGrowth = 0;
		for (int i=0;i<allTraits.size();i++){
			for (int j=0;j<allTraits.get(i).traitDescriptor.size();i++){
				if (allTraits.get(i).traitDescriptor.get(j)=="maxHPGrowth"){
					newMaxHPGrowth=newMaxHPGrowth+allTraits.get(i).traitNumber.get(j);
				}
				if (allTraits.get(i).traitDescriptor.get(j)=="maxMPGrowth"){
					newMaxMPGrowth=newMaxMPGrowth+allTraits.get(i).traitNumber.get(j);
				}
				if (allTraits.get(i).traitDescriptor.get(j)=="maxDPGrowth"){
					newMaxDPGrowth=newMaxDPGrowth+allTraits.get(i).traitNumber.get(j);
				}
				if (allTraits.get(i).traitDescriptor.get(j)=="maxSTRGrowth"){
					newMaxSTRGrowth=newMaxSTRGrowth+allTraits.get(i).traitNumber.get(j);
				}
				if (allTraits.get(i).traitDescriptor.get(j)=="maxDEXGrowth"){
					newMaxDEXGrowth=newMaxDEXGrowth+allTraits.get(i).traitNumber.get(j);
				}
				if (allTraits.get(i).traitDescriptor.get(j)=="maxFITGrowth"){
					newMaxFITGrowth=newMaxFITGrowth+allTraits.get(i).traitNumber.get(j);
				}
				if (allTraits.get(i).traitDescriptor.get(j)=="maxMAGGrowth"){
					newMaxMAGGrowth=newMaxMAGGrowth+allTraits.get(i).traitNumber.get(j);
				}
			}
		}
		this.maxHPGrowth=newMaxHPGrowth;
		this.maxMPGrowth=newMaxMPGrowth;
		this.maxDPGrowth=newMaxDPGrowth;
		this.maxSTRGrowth=newMaxSTRGrowth;
		this.maxDEXGrowth=newMaxDEXGrowth;
		this.maxFITGrowth=newMaxFITGrowth;
		this.maxMAGGrowth=newMaxMAGGrowth;
	}
	//This updates the growth rate for the Entity.
