	void calculateGrowthRates(){
		
	}
	//The entity must meet the level required to cast the spell for it to be added.
	void addSpell(Spell spell){
		if (this.level>=spell.requiredLevelToLearn){
			this.knownSpells.add(spell);
		}
	}
	void removeSpellsWithString(String spellName){
		for (int i=0;i<knownSpells.size();i++){
			if (knownSpells.get(i).name==spellName){
				knownSpells.remove(i);
			}
		}
	}
	void addTrait(Trait trait){
		this.uniqueTraits.add(trait);
	}
	void addEffect(Effect effect){
		this.currentEffects.add(effect);
	}
	//The below method makes the current traits and abilities of the Entity easily accessible.
	//This method generally only needs to be called after the Entity changes class, race, type, or level.
	void consolidateTraitsAndAbilities(){
		if (allAbilities!=null){
			allAbilities.clear();
			for (int i=0;i<this.unitClass.abilities.size();i++){
				if (this.level>=this.unitClass.requiredLevelForAbility.get(i)){
					allAbilities.add(unitClass.abilities.get(i));
				}
			}
			for (int i=0;i<this.unitRace.abilities.size();i++){
				allAbilities.add(unitRace.abilities.get(i));
			}
			for (int i=0;i<this.unitType.abilities.size();i++){
				allAbilities.add(unitType.abilities.get(i));
			}
			for (int i=0;i<this.uniqueAbilities.size();i++){
				allAbilities.add(uniqueAbilities.get(i));
			}
		}
		if (allAbilities!=null&&allAttacks!=null){
			allAttacks.clear();
			for (int i=0;i<this.allAbilities.size();i++){
				if (allAbilities.get(i).treatAsAttack==true){
					allAttacks.add(allAbilities.get(i));
					allAbilities.remove(i);
				}
			}
		}
		if (allTraits!=null){
			allTraits.clear();
			for (int i=0;i<this.unitClass.traits.size();i++){
				if (this.level>=this.unitClass.requiredLevelForTrait.get(i)){
					allTraits.add(unitClass.traits.get(i));
				}
			}
			for (int i=0;i<this.unitRace.traits.size();i++){
				allTraits.add(unitRace.traits.get(i));
			}
			for (int i=0;i<this.unitType.traits.size();i++){
				allTraits.add(unitType.traits.get(i));
			}
			for (int i=0;i<this.uniqueTraits.size();i++){
				allTraits.add(uniqueTraits.get(i));
			}
		}
	}
	//Target the space target, apply all effects, space effects, and damage to the area it encompasses.
	void useAbilityOnTarget(Ability ability, gameCube target){
		ArrayList<gameCube> affectedArea = gameKernel.rangeArea(target, ability.baseMaxArea, ability.baseMinArea, -1,true,false);
		ArrayList<Entity> affectedEntities = new ArrayList<Entity>();
		ArrayList<Effect> effectsToApply = ability.abilityEffects;
		ArrayList<spaceEffect> spaceEffectsToApply = ability.abilitySpaceEffects;
		//Round up the affectedEntities. Meanwhile, apply spaceEffects.
		for (int i=0;i<affectedArea.size();i++){
			if (affectedArea.get(i).getEntity()!=null){
				affectedEntities.add(affectedArea.get(i).getEntity());
			}
			for (int j=0;j<spaceEffectsToApply.size();j++){
				affectedArea.get(i).activeSpaceEffects.add(new spaceEffect(spaceEffectsToApply.get(j),this));
			}
		}
		for (int i=0;i<affectedEntities.size();i++){
			affectedEntities.get(i).HP=affectedEntities.get(i).HP-ability.baseStrength;
			for (int j=0;j<effectsToApply.size();j++){
				affectedEntities.get(i).currentEffects.add(new Effect(effectsToApply.get(j),this));
			}
		}
	}
	//The below method decrements the duration of all current effects. If the duration becomes less than 0 this way,
	//it is removed from the unit.
	void updateEffectDurations(){
		for (int i=0;i<this.currentEffects.size();i++){
			currentEffects.get(i).duration--;
			if (currentEffects.get(i).duration<0){
				currentEffects.remove(i);
			}
		}
	}
